import createHome from"./home.js";import createMenu from"./menu.js";import createContact from"./contact.js";import"./styles.css";function setActiveButton(t){document.querySelectorAll("nav button").forEach((t=>t.classList.remove("active"))),t.classList.add("active")}function clearContent(){document.getElementById("content").innerHTML=""}function loadPage(t){clearContent(),document.getElementById("content").appendChild(t())}document.addEventListener("DOMContentLoaded",(()=>{const t=document.querySelector("nav button:nth-child(1)"),e=document.querySelector("nav button:nth-child(2)"),n=document.querySelector("nav button:nth-child(3)");t.addEventListener("click",(e=>{setActiveButton(t),loadPage(createHome)})),e.addEventListener("click",(t=>{setActiveButton(e),loadPage(createMenu)})),n.addEventListener("click",(t=>{setActiveButton(n),loadPage(createContact)})),loadPage(createHome),setActiveButton(t)}));